import { Wheel } from "react-custom-roulette";
import { useState } from "react";
import * as Styled from "./style"

export const Wheels = () => {
  const data = [
    { option: "0", style: { backgroundColor: "green", textColor: "white" } },
    { option: "1", style: { backgroundColor: "red", textColor: "white" } },
    { option: "3", style: { backgroundColor: "blue", textColor: "white" } },
    { option: "4", style: { backgroundColor: "pink", textColor: "white" } },
    { option: "5", style: { backgroundColor: "orrange", textColor: "white" } },
    { option: "6", style: { backgroundColor: "gray", textColor: "white" } },
    { option: "7", style: { backgroundColor: "black", textColor: "white" } },
    { option: "8", style: { backgroundColor: "red", textColor: "white" } },
  ];
  const [x, sx] = useState<boolean>(false);
  const [result, setResult] = useState<any>("");
const [showResult, setShowResult]= useState<any>("");
  function stopSpin() {
    sx(false);
    setShowResult(data[result].option);
  }

  function getResult() {
    const res = Math.floor(Math.random() * data.length);
    console.log(data[res]);
    setResult(res);
  }
  return (
      <>
      <div>
          <h1>Spinning</h1>
          <h1>Wheel</h1>
      </div>
    <Styled.WheelContainer>
      <Wheel
        prizeNumber={result}
        mustStartSpinning={x}
        data={data}
        backgroundColors={["#3e3e3e", "#df3428"]}
        textColors={["#ffffff"]}
        onStopSpinning={() => stopSpin()}
        radiusLineWidth={2}
        outerBorderWidth={2}
        innerRadius={5}
        
     
      />
      <Styled.SpinBtn
        onClick={() => {
          sx(true);
          getResult();
        }}
      >
        SPIN
      </Styled.SpinBtn>
      <Styled.SpanTag>You Won: {showResult}</Styled.SpanTag>
    </Styled.WheelContainer>
    </>
  );
};
