import styled from "styled-components";

export const WheelContainer = styled.div`
display:flex;
`

export const SpinBtn = styled.button`
height:80px;
width:80px;
display:flex;
justify-content:center;
align-items:center;
outline: none;
    z-index: 5;
    position: absolute;
top: 183px;
left: 183px;
border-radius: 50%;
border: none;

`
