import styled from "styled-components";

export const WheelContainer = styled.div`
display:flex;
position: relative;
margin-top: 140px;
margin-left: 80px;
`

export const SpinBtn = styled.button`
height:80px;
width:80px;
display:flex;
justify-content:center;
align-items:center;
outline: none;
z-index: 5;
position: absolute;
top: 183px;
left: 183px;
border-radius: 50%;
border: none;





`

export const SpanTag = styled.span`
    position: absolute;
    top: 100%;
    left: 35rem;
    font-size: xx-large;
    `
