import styled from "styled-components";

export const WheelContainer = styled.div`
display:flex;
position: relative;
margin-top: 140px;
margin-left: 80px;
`

export const SpinBtn = styled.button`
height:80px;
width:80px;
display:flex;
justify-content:center;
align-items:center;
outline: none;
z-index: 5;
position: absolute;
top: 183px;
left: 183px;
border-radius: 50%;
border: none;


--clr-neon: hsl(317 100% 54%);
--clr-bg: hsl(323 21% 16%);

  cursor: pointer;
  color: var(--clr-neon);
  text-decoration: none;
  text-shadow: 0 0 0.125em hsl(0 0% 100% / 0.35), 0 0 0.45em currentColor;
  box-shadow: inset 0 0 0.5em 0 var(--clr-neon), 0 0 0.5em 0 var(--clr-neon);

  border: var(--clr-neon) 4px solid; 

  &:before{
    pointer-events: none;
  content: "";
  position: absolute;

  background: var(--clr-neon);
  top: 120%;
  left: 0;
  height: 100%;
  width: 100%;
  transform: perspective(1em) rotateX(40deg) scale(1, 0.35);
  filter: blur(1.5em);
  opacity: .7;
  }

  &:after{
    pointer-events: none;
  content: "";
  position: absolute;

  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  background: var(--clr-neon);
  box-shadow: 0 0 .75em .25em var(--clr-neon);
  opacity: 0;
  z-index: -1;
  transition: opacity .1s linear;
  }

  &:hover{
    color: var(--clr-bg);
  text-shadow: none;
  }

  &:focus{
    color: var(--clr-bg);
  text-shadow: none;
  }

  &:hover::after{
    opacity: 1;
  }

  &:hover::before{
    opacity: 1;
  }

  &:focus::before{
    opacity: 1;
  }

  &:focus::after{
    opacity: 1;
  }

`

export const SpanTag = styled.span`
    position: absolute;
    top: 100%;
    left: 35rem;
    font-size: xx-large;
    `
